INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (3, 4, 'Hoan Kiem Lake', '', 'hoan-kiem-lake', '<p style="text-align: justify;"><strong>Hoan Kiem Lake</strong> (Vietnamese: HÃ¡Â»â€œ Ho&agrave;n KiÃ¡ÂºÂ¿m, meaning "Lake of the Returned Sword" or "Lake of the Restored Sword", also known as HÃ¡Â»â€œ GÃ†Â°Ã†Â¡m - Sword Lake) is a lake in the historical center of Hanoi, the capital city of Vietnam. The lake is one of the major scenic spots in the city and serves as a focal point for its public life.</p>', '<h2>History</h2>\r\n<p style="text-align: justify;">According to the legend, emperor L&ecirc; LÃ¡Â»Â£i handed a magic sword called Heaven''s Will which brought him victory in his revolt against the Chinese Ming Dynasty back to the Golden Turtle God (Kim Qui) in the lake and hence gave it its present name (the lake was formerly known as "Luc Thuy" meaning "Green Water"). The Tortoise Tower (Thap R&ugrave;a) standing on a small island near the center of lake is linked to the legend.</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7c880db619c_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Turtle Tower</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">Large soft-shell turtles, either of the species Rafetus swinhoei or a separate species named Rafetus leloii in honor of the emperor, have been sighted in the lake. The species is critically endangered and the number of individuals in the lake is unclear.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7c880ed46f3_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>The Huc Bridge</em></p>\r\n<p style="text-align: justify;"><br />Near the northern shore of the lake lies Jade Island on which the Ngoc Son Temple (Jade Mountain Temple) stands. The temple was erected in the 18th century. It honors the 13-century military leader Tran Hung Dao who distinguished himself in the fight against the Yuan Dynasty, Van Xuong, a scholar, and Nguyen Van Sieu, a Confucian master and famous writer in charge of repairs made to the temple in 1864. Jade Island is connected to the shore by the wooden red-painted Huc Bridge (The Huc, meaning Morning Sunlight Bridge).</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_small.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_crop.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_medium.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7c881016a8d_large.jpg', 'active', 2, '11:44:51', 1, 'admin', '18:25:43', 1, 'admin', 1, 'admin', '11:44:59', 0, 0, 0, 0, NULL, 0, 'en_US');
