INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (11, 9, 'Steamed cake', '', 'steamed-cake', '<p style="text-align: justify;">B&aacute;nh chÃ†Â°ng (steamed cake) is a traditional Vietnamese cake which is made from glutinous rice, mung bean, pork and other ingredients. Its origin is told by the legend of Lang Li&ecirc;u, a prince of the sixth H&ugrave;ng VÃ†Â°Ã†Â¡ng, who became H&ugrave;ng VÃ†Â°Ã†Â¡ng''s successor thank to his creation of b&aacute;nh chÃ†Â°ng and b&aacute;nh d&agrave;y, which symbolized respectively the Earth and the Sky. Considered an essential element of the family altar on the occasion of TÃ¡ÂºÂ¿t, the making and eating b&aacute;nh chÃ†Â°ng during this time is a well-preserved tradition of Vietnamese people. Beside the TÃ¡ÂºÂ¿t holiday, b&aacute;nh chÃ†Â°ng is also tasted all year round as a delicacy of Vietnamese cuisine and one of the national dishes of Vietnam besides nem r&aacute;n and phÃ¡Â»Å¸.</p>', '<h2 style="text-align: justify;"><span id="Legend_and_symbol" class="mw-headline">Legend and symbol</span></h2>\r\n<p style="text-align: justify;">According to the book LÃ„Â©nh Nam ch&iacute;ch qu&aacute;i (Extraordinary stories of LÃ„Â©nh Nam) published in 1695, the creation of b&aacute;nh chÃ†Â°ng was credited to Lang Li&ecirc;u, a prince of the sixth H&ugrave;ng VÃ†Â°Ã†Â¡ng. It was said that after the victory against the army of Shang Dynasty, in choosing a successor among his sons, H&ugrave;ng VÃ†Â°Ã†Â¡ng decided to carry out a competition in which each prince brought a delicacy representing the sincerity for the ancestors on the occasion of the TÃ¡ÂºÂ¿t, whoever could introduce the most delicious dish for the altar would become the next ruler of the country.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">An unwrapped, cut into 8 pieces b&aacute;nh chÃ†Â°ng ready to serve</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">While other princes tried to find the rare and delicious foods from forest and sea, the eighteenth prince Lang Li&ecirc;u, who was the poorest son of H&ugrave;ng VÃ†Â°Ã†Â¡ng, could not afford those luxury dishes and had to content with the everyday ingredients ingredients such as rice or pork. Finally, he created one cake in the square form of earth called b&aacute;nh chÃ†Â°ng and one in the round form of sky called b&aacute;nh d&agrave;y from these simple ingredients. In tasting the dishes offered by his son, H&ugrave;ng VÃ†Â°Ã†Â¡ng found b&aacute;nh chÃ†Â°ng and b&aacute;nh d&agrave;y not only delicious but also a fine respresentation of the respect for ancestors. Therefore he decided to cede the throne to Lang Li&ecirc;u and b&aacute;nh chÃ†Â°ng, b&aacute;nh d&agrave;y became traditional foods during the TÃ¡ÂºÂ¿t.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chÃ†Â°ng is served with gi&ograve; lÃ¡Â»Â¥a and other dishes</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;">&nbsp;</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_small.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_crop.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_medium.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_large.jpg', 'active', 5, '14:10:47', 1, 'admin', '14:19:19', 1, 'admin', 1, 'admin', '14:13:47', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (12, 20, 'BÃƒÂ¡nh chÃ†Â°ng', '', 'banh-chung', '<p>B&aacute;nh chÃ†Â°ng l&agrave; mÃ¡Â»â„¢t loÃ¡ÂºÂ¡i b&aacute;nh truyÃ¡Â»ï¿½n thÃ¡Â»â€˜ng cÃ¡Â»Â§a d&acirc;n tÃ¡Â»â„¢c ViÃ¡Â»â€¡t nhÃ¡ÂºÂ±m thÃ¡Â»Æ’ hiÃ¡Â»â€¡n l&ograve;ng biÃ¡ÂºÂ¿t Ã†Â¡n cÃ¡Â»Â§a con ch&aacute;u Ã„â€˜Ã¡Â»â€˜i vÃ¡Â»â€ºi cha &ocirc;ng v&agrave; Ã„â€˜Ã¡ÂºÂ¥t trÃ¡Â»ï¿½i xÃ¡Â»Â© sÃ¡Â»Å¸. Nguy&ecirc;n liÃ¡Â»â€¡u l&agrave;m b&aacute;nh bao gÃ¡Â»â€œm gÃ¡ÂºÂ¡o nÃ¡ÂºÂ¿p, Ã„â€˜Ã¡ÂºÂ­u xanh, thÃ¡Â»â€¹t lÃ¡Â»Â£n, l&aacute; dong v&agrave; b&aacute;nh thÃ†Â°Ã¡Â»ï¿½ng Ã„â€˜Ã†Â°Ã¡Â»Â£c l&agrave;m v&agrave;o c&aacute;c dÃ¡Â»â€¹p TÃ¡ÂºÂ¿t cÃ¡Â»â€¢ truyÃ¡Â»ï¿½n cÃ¡Â»Â§a d&acirc;n tÃ¡Â»â„¢c ViÃ¡Â»â€¡t, cÃ…Â©ng nhÃ†Â° ng&agrave;y giÃ¡Â»â€¢ tÃ¡Â»â€¢ H&ugrave;ng VÃ†Â°Ã†Â¡ng) (m&ugrave;ng 10 th&aacute;ng 3 &acirc;m lÃ¡Â»â€¹ch).</p>', '<h2><span id="S.E1.BB.B1_t.C3.ADch" class="mw-headline">SÃ¡Â»Â± t&iacute;ch</span></h2>\r\n<p style="text-align: justify;">L&agrave; loÃ¡ÂºÂ¡i b&aacute;nh duy nhÃ¡ÂºÂ¥t c&oacute; lÃ¡Â»â€¹ch sÃ¡Â»Â­ l&acirc;u Ã„â€˜Ã¡Â»ï¿½i trong Ã¡ÂºÂ©m thÃ¡Â»Â±c truyÃ¡Â»ï¿½n thÃ¡Â»â€˜ng ViÃ¡Â»â€¡t Nam c&ograve;n Ã„â€˜Ã†Â°Ã¡Â»Â£c sÃ¡Â»Â­ s&aacute;ch nhÃ¡ÂºÂ¯c lÃ¡ÂºÂ¡i, b&aacute;nh chÃ†Â°ng c&oacute; vÃ¡Â»â€¹ tr&iacute; Ã„â€˜Ã¡ÂºÂ·c biÃ¡Â»â€¡t trong t&acirc;m thÃ¡Â»Â©c cÃ¡Â»Â§a cÃ¡Â»â„¢ng Ã„â€˜Ã¡Â»â€œng ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t v&agrave; nguÃ¡Â»â€œn gÃ¡Â»â€˜c cÃ¡Â»Â§a n&oacute; c&oacute; thÃ¡Â»Æ’ truy nguy&ecirc;n vÃ¡Â»ï¿½ truyÃ¡Â»ï¿½n thuyÃ¡ÂºÂ¿t li&ecirc;n quan Ã„â€˜Ã¡ÂºÂ¿n ho&agrave;ng tÃ¡Â»Â­ Lang Li&ecirc;u v&agrave;o Ã„â€˜Ã¡Â»ï¿½i vua H&ugrave;ng thÃ¡Â»Â© 6. SÃ¡Â»Â± t&iacute;ch tr&ecirc;n muÃ¡Â»â€˜n. nhÃ¡ÂºÂ¯c nhÃ¡Â»Å¸ con ch&aacute;u vÃ¡Â»ï¿½ truyÃ¡Â»ï¿½n thÃ¡Â»â€˜ng cÃ¡Â»Â§a d&acirc;n tÃ¡Â»â„¢c; l&agrave; lÃ¡Â»ï¿½i giÃ¡ÂºÂ£i th&iacute;ch &yacute; nghÃ„Â©a cÃ…Â©ng nhÃ†Â° nguÃ¡Â»â€œn cÃ¡Â»â„¢i cÃ¡Â»Â§a cÃ¡Â»Â§a B&aacute;nh ChÃ†Â°ng, B&aacute;nh GiÃ¡ÂºÂ§y trong vÃ„Æ’n h&oacute;a, Ã„â€˜Ã¡Â»â€œng thÃ¡Â»ï¿½i nhÃ¡ÂºÂ¥n mÃ¡ÂºÂ¡nh tÃ¡ÂºÂ§m quan trÃ¡Â»ï¿½ng cÃ¡Â»Â§a c&acirc;y l&uacute;a v&agrave; thi&ecirc;n nhi&ecirc;n trong nÃ¡Â»ï¿½n vÃ„Æ’n ho&aacute; l&uacute;a nÃ†Â°Ã¡Â»â€ºc.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cac4720ab1_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">MÃ¡Â»â„¢t chiÃ¡ÂºÂ¿c b&aacute;nh chÃ†Â°ng vu&ocirc;ng v&agrave; mÃ¡Â»â„¢t chiÃ¡ÂºÂ¿c b&aacute;nh chÃ†Â°ng t&agrave;y vÃ¡Â»Â«a Ã„â€˜Ã†Â°Ã¡Â»Â£c g&oacute;i</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Quan_ni.E1.BB.87m_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">Quan niÃ¡Â»â€¡m truyÃ¡Â»ï¿½n thÃ¡Â»â€˜ng</span></h2>\r\n<p style="text-align: justify;">Theo quan niÃ¡Â»â€¡m phÃ¡Â»â€¢ biÃ¡ÂºÂ¿n hiÃ¡Â»â€¡n nay, c&ugrave;ng vÃ¡Â»â€ºi b&aacute;nh giÃ¡ÂºÂ§y, b&aacute;nh chÃ†Â°ng tÃ†Â°Ã¡Â»Â£ng trÃ†Â°ng cho quan niÃ¡Â»â€¡m vÃ¡Â»ï¿½ vÃ…Â© trÃ¡Â»Â¥ cÃ¡Â»Â§a ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t xÃ†Â°a. B&aacute;nh c&oacute; m&agrave;u xanh l&aacute; c&acirc;y, h&igrave;nh vu&ocirc;ng, Ã„â€˜Ã†Â°Ã¡Â»Â£c coi l&agrave; Ã„â€˜Ã¡ÂºÂ·c trÃ†Â°ng cho Ã„â€˜Ã¡ÂºÂ¥t trong t&iacute;n ngÃ†Â°Ã¡Â»Â¡ng cÃ¡Â»Â§a ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t cÃ¡Â»â€¢ v&agrave; c&aacute;c d&acirc;n tÃ¡Â»â„¢c kh&aacute;c trong khu vÃ¡Â»Â±c ch&acirc;u &Aacute;. Tuy nhi&ecirc;n, theo Gi&aacute;o sÃ†Â° TrÃ¡ÂºÂ§n QuÃ¡Â»â€˜c VÃ†Â°Ã¡Â»Â£ng, b&aacute;nh chÃ†Â°ng nguy&ecirc;n thÃ¡Â»Â§y c&oacute; h&igrave;nh tr&ograve;n v&agrave; d&agrave;i, giÃ¡Â»â€˜ng nhÃ†Â° b&aacute;nh t&eacute;t, [Ã„â€˜Ã¡Â»â€œng thÃ¡Â»ï¿½i b&aacute;nh chÃ†Â°ng v&agrave; b&aacute;nh giÃ¡ÂºÂ§y tÃ†Â°Ã¡Â»Â£ng trÃ†Â°ng cho nam v&agrave; nÃ¡Â»Â¯ trong t&iacute;n ngÃ†Â°Ã¡Â»Â¡ng phÃ¡Â»â€œn thÃ¡Â»Â±c ViÃ¡Â»â€¡t Nam. B&aacute;nh t&eacute;t, thay thÃ¡ÂºÂ¿ vÃ¡Â»â€¹ tr&iacute; cÃ¡Â»Â§a b&aacute;nh chÃ†Â°ng v&agrave;o c&aacute;c dÃ¡Â»â€¹p TÃ¡ÂºÂ¿t trong cÃ¡Â»â„¢ng Ã„â€˜Ã¡Â»â€œng ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t Ã¡Â»Å¸ miÃ¡Â»ï¿½n nam ViÃ¡Â»â€¡t Nam, theo TrÃ¡ÂºÂ§n QuÃ¡Â»â€˜c VÃ†Â°Ã¡Â»Â£ng l&agrave; dÃ¡ÂºÂ¡ng nguy&ecirc;n thÃ¡Â»Â§y cÃ¡Â»Â§a b&aacute;nh chÃ†Â°ng.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cac46114ad_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;">B&aacute;nh chÃ†Â°ng v&agrave; gi&ograve; lÃ¡Â»Â¥a</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">G&oacute;i v&agrave; nÃ¡ÂºÂ¥u b&aacute;nh chÃ†Â°ng, ngÃ¡Â»â€œi canh nÃ¡Â»â€œi b&aacute;nh chÃ†Â°ng tr&ecirc;n bÃ¡ÂºÂ¿p lÃ¡Â»Â­a Ã„â€˜&atilde; trÃ¡Â»Å¸ th&agrave;nh mÃ¡Â»â„¢t tÃ¡ÂºÂ­p qu&aacute;n, vÃ„Æ’n h&oacute;a sÃ¡Â»â€˜ng trong c&aacute;c gia Ã„â€˜&igrave;nh ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t mÃ¡Â»â€”i dÃ¡Â»â€¹p tÃ¡ÂºÂ¿t Ã„â€˜Ã¡ÂºÂ¿n xu&acirc;n vÃ¡Â»ï¿½.</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_small.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_crop.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_medium.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7caba005603_large.jpg', 'active', 1, '14:32:18', 1, 'admin', '15:41:43', 1, 'admin', 1, 'admin', '14:32:28', 0, 0, 0, 0, NULL, 0, 'vi_VN');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (13, 2, 'Vietnamese cuisine', '', 'vietnamese-cuisine', '<p style="text-align: justify;">Vietnamese cuisine is a style of cooking derived from Vietnam with fish sauce, soy sauce, rice, fresh herbs, fruits and vegetables all commonly used. Vietnamese recipes utilize a diverse range of herbs, including lemongrass, mint, Vietnamese mint, long coriander and Thai basil leaves. Traditional Vietnamese cooking is greatly admired for freshness of the ingredients and for the healthy eating style.</p>', '<p style="text-align: justify;">The most common meats used in Vietnamese cuisine are beef, pork, chicken, fish, and various kinds of seafood. The Vietnamese also have a strong vegetarian tradition influenced by Buddhist values.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Hu tieu kho, braised rice noodle dish</em></p>\r\n<h2 style="text-align: justify;"><span id="Regional_variations" class="mw-headline">Regional variations</span></h2>\r\n<p style="text-align: justify;">As a whole, Vietnamese cuisine contains strong influences mainly from Cantonese cooking with a bit of a French twist to it. This is because unlike many other Indochinese nations, which are strongly influences by the culture of India, Vietnam is the only country in which Chinese-like cultures remained strong, since that it was first ruled by China for at least a 1000 years, followed by control from France centuries later for a 100 years. However, depending on the region, it can be divided into three categories, each pertaining to a distinct geographical region. With Northern Vietnam being the cradle of Vietnamese civilization, many of Vietnam''s most notable dishes such as phÃ¡Â»Å¸ and b&aacute;nh cuÃ¡Â»â€˜n can trace their origin to the North. Northern cuisine is more traditional and less diverse in choosing spices and ingredients.</p>\r\n<p style="text-align: justify;">The cuisine of South Vietnam has historically been influenced by the influx of southern Chinese immigrants, French colonists and other nationalities. Southerners prefer sweet flavors in many dishes.[citation needed] As a region of perhaps greater diversity in terms of external influences, the South''s cuisine uses a wider variety of herbs.</p>\r\n<p style="text-align: justify;">The cuisine of Central Vietnam is distinct from the cuisines of both the Northern and Southern regions in its use of many small side dishes. For a while the country was ruled from HuÃ¡ÂºÂ¿ in Central Vietnam, so that most of the dishes were made small and dedicated to the kings. Compared to its counterparts, its cuisine is more spicy.</p>\r\n<p style="text-align: justify;">One most common French influence is the use of baguettes in Vietnamese meals. Vietnamese sandwiches are traditionally made with baguettes, and sometimes soups such as canh chua are served with a baguette on the side.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2 style="text-align: justify;"><span id="Typical_Vietnamese_family_meals" class="mw-headline">Typical Vietnamese family meals</span></h2>\r\n<p style="text-align: justify;">A typical meal for the average Vietnamese family would include:<br /><br />- Individual bowls of rice<br />- Meat, fish or seafood (grilled, boiled, steamed, stewed or stir fried with vegetables)<br />- Stir-fried, raw, pickled or steamed vegetables<br />- Canh (a clear broth with vegetables and often meat or seafood) or other Vietnamese-style soup<br />- Prepared fish sauce and/or soy sauce for dipping, to which garlic, chili, ginger or lime juice are sometimes added according to taste.<br /><br />All dishes apart from the individual bowls of rice are communal and to be shared.</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_small.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_medium.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_large.jpg', 'active', 4, '14:34:47', 1, 'admin', '15:33:22', 1, 'admin', 1, 'admin', '14:34:54', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (14, 17, 'Ã¡ÂºÂ¨m thÃ¡Â»Â±c ViÃ¡Â»â€¡t Nam', '', 'am-thuc-viet-nam', '<p>Ã¡ÂºÂ¨m thÃ¡Â»Â±c ViÃ¡Â»â€¡t Nam l&agrave; c&aacute;ch gÃ¡Â»ï¿½i cÃ¡Â»Â§a phÃ†Â°Ã†Â¡ng thÃ¡Â»Â©c chÃ¡ÂºÂ¿ biÃ¡ÂºÂ¿n m&oacute;n Ã„Æ’n, nguy&ecirc;n l&yacute; phÃ¡Â»â€˜i trÃ¡Â»â„¢n gia vÃ¡Â»â€¹ v&agrave; nhÃ¡Â»Â¯ng th&oacute;i quen Ã„Æ’n uÃ¡Â»â€˜ng n&oacute;i chung cÃ¡Â»Â§a mÃ¡Â»ï¿½i ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t tr&ecirc;n Ã„â€˜Ã¡ÂºÂ¥t nÃ†Â°Ã¡Â»â€ºc ViÃ¡Â»â€¡t Nam. Tuy hÃ¡ÂºÂ§u nhÃ†Â° c&oacute; &iacute;t nhiÃ¡Â»ï¿½u c&oacute; sÃ¡Â»Â± kh&aacute;c biÃ¡Â»â€¡t, Ã¡ÂºÂ©m thÃ¡Â»Â±c ViÃ¡Â»â€¡t Nam vÃ¡ÂºÂ«n bao h&agrave;m &yacute; nghÃ„Â©a kh&aacute;i qu&aacute;t nhÃ¡ÂºÂ¥t Ã„â€˜Ã¡Â»Æ’ chÃ¡Â»â€° tÃ¡ÂºÂ¥t cÃ¡ÂºÂ£ nhÃ¡Â»Â¯ng m&oacute;n Ã„Æ’n phÃ¡Â»â€¢ biÃ¡ÂºÂ¿n trong cÃ¡Â»â„¢ng Ã„â€˜Ã¡Â»â€œng c&aacute;c d&acirc;n tÃ¡Â»â„¢c thiÃ¡Â»Æ’u sÃ¡Â»â€˜ nhÃ†Â°ng Ã„â€˜&atilde; tÃ†Â°Ã†Â¡ng Ã„â€˜Ã¡Â»â€˜i phÃ¡Â»â€¢ th&ocirc;ng trong cÃ¡Â»â„¢ng Ã„â€˜Ã¡Â»â€œng ngÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t.</p>', '<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cb06864644_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>HÃ¡Â»Â§ tiÃ¡ÂºÂ¿u<br /></em></p>\r\n<h3><span id="B.E1.BB.AFa_.C4.83n_gia_.C4.91.C3.ACnh_Vi.E1.BB.87t_Nam_truy.E1.BB.81n_th.E1.BB.91ng" class="mw-headline">BÃ¡Â»Â¯a Ã„Æ’n gia Ã„â€˜&igrave;nh ViÃ¡Â»â€¡t Nam truyÃ¡Â»ï¿½n thÃ¡Â»â€˜ng</span></h3>\r\n<p>NgÃ†Â°Ã¡Â»ï¿½i ViÃ¡Â»â€¡t thÃ†Â°Ã¡Â»ï¿½ng Ã„Æ’n phÃ¡Â»Â¥ v&agrave;o buÃ¡Â»â€¢i s&aacute;ng vÃ¡Â»â€ºi c&aacute;c thÃ¡Â»Â©c qu&agrave; vÃ¡ÂºÂ·t