INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (33, 2, 'Canh chua', '', 'canh-chua', '<p>Canh chua (literally "sour soup") is a sour soup indigenous to the Mekong River region of southern Vietnam.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p>It is typically made with fish from the Mekong River, pineapple, amanes (and sometimes also other vegetables such as okra or bÃ¡ÂºÂ¡c h&agrave;), and bean sprouts, in a tamarind-flavored broth. It is garnished with the lemony-scented herb ng&ograve; &ocirc;m (Limnophila aromatica), caramelized garlic, and chopped scallions, as well as other herbs, according to the specific variety of canh chua; these other herbs may include rau rÃ„Æ’m (Vietnamese coriander), ng&ograve; gai (long coriander), and rau quÃ¡ÂºÂ¿ (Thai basil).<br /><br />The sour taste of the soup comes from tamarind, which is mixed with a small amount of hot water; the mixture is then stirred for a few moments to release all the essence, and the liquid (minus the tamarind seeds and other solids, which are discarded) is then added to the soup.<br /><br />When made in style of a hot pot, canh chua is called lÃ¡ÂºÂ©u canh chua.</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cda8d94a50_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cda8d94a50_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cda8d94a