INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (35, 2, 'BÃƒÂ¡nh Ã„â€˜ÃƒÂºc', '', 'banh-duc', '<p>B&aacute;nh Ã„â€˜&uacute;c is a term used to refer to two different varieties of Vietnamese b&aacute;nh, or cake.</p>', '<h2>Northern Vietnamese version</h2>\r\n<p style="text-align: justify;">In northern Vietnam, b&aacute;nh Ã„â€˜&uacute;c is a cake made from either non-glutinous rice flour or corn flour. It is white in color and has a soft texture and mild flavour.photo It is typically garnished with savory ingredients such as ground pork, t&ocirc;m chÃ¡ÂºÂ¥y (grilled ground shrimp),photo fried onions, sesame seeds, salt, peanuts, lime juice, and soy sauce or fish sauce.photo Although it may be eaten on its own, it may also be served hot, accompanied by steamed meat or mushrooms.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cdd066d361_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of northern Vietnamese b&aacute;nh Ã„â€˜&uacute;c thÃ¡Â»â€¹t (b&aacute;nh Ã„â€˜&uacute;c with ground pork and fish sauce)</em></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">B&aacute;nh Ã„â€˜&uacute;c is available at small stalls and is eaten throughout the day.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h2><span id="Southern_Vietnamese_version" class="mw-headline">Southern Vietnamese version</span></h2>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A plate of southern Vietnamese b&aacute;nh Ã„â€˜&uacute;c colored with Pandanus amaryllifolius leaf extract, and topped with sweetened coconut milk and sesame seeds</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In southern Vietnam, b&aacute;nh Ã„â€˜&uacute;c is a dessert made from non-glutinous rice flour. It takes the form of gelatinous blocks that are often colored green by the addition of Pandanus amaryllifolius leaf extract. It is cooked by boiling the ingredients and allowing them to cool, solidifying into a jelly-like sheetphoto that is then cut into blocks.</p>', 'Wikipedia', '', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_square.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_thumbnail.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_small.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_crop.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_medium.jpg', 'http://localhost/amancms/upload/news/admin/2010/08/4c7cdc71e1d42_large.jpg', 'active', 3, '17:45:30', 1, 'admin', '18:20:23', 1, 'admin', 1, 'admin', '17:45:37', 0, 0, 0, 0, NULL, 0, 'en_US');
INSERT INTO news_article (article_id, category_id, title, sub_title, slug, description, content, author, icons, image_square, image_thumbnail, image_small, image_crop, image_medium, image_large, status, num_views, created_date, created_user_id, created_user_name, updated_date, updated_user_id, updated_user_name, activate_user_id, activate_user_name, activate_date, allow_comment, num_comments, is_hot, ordering, show_date, sticky, language) VALUES (36, 17, 'BÃƒÂ¡nh Ã„â€˜ÃƒÂºc', '', 'banh-duc', '<p style="text-align: justify;">B&aacute;nh Ã„â€˜&uacute;c l&agrave; mÃ¡Â»â„¢t loÃ¡ÂºÂ¡i b&aacute;nh cÃ¡Â»Â§a ViÃ¡Â»â€¡t Nam, thÃ†Â°Ã¡Â»ï¿½ng Ã„â€˜Ã†Â°Ã¡Â»Â£c l&agrave;m bÃ¡ÂºÂ±ng bÃ¡Â»â„¢t gÃ¡ÂºÂ¡o (tÃ¡ÂºÂ¡i miÃ¡Â»ï¿½n BÃ¡ÂºÂ¯c v&agrave; miÃ¡Â»ï¿½n Trung) hoÃ¡ÂºÂ·c bÃ¡Â»â„¢t nÃ„Æ’ng (miÃ¡Â»ï¿½n Nam) vÃ¡Â»â€ºi mÃ¡Â»â„¢t sÃ¡Â»â€˜ gia vÃ¡Â»â€¹. B&aacute;nh Ã„â€˜Ã†Â°Ã¡Â»Â£c l&agrave;m th&agrave;nh tÃ¡ÂºÂ¥m to, khi Ã„Æ’n th&igrave; cÃ¡ÂºÂ¯t nhÃ¡Â»ï¿½ th&agrave;nh miÃ¡ÂºÂ¿ng t&ugrave;y th&iacute;ch.</p>', '<p style="text-align: justify;">L&agrave; m&oacute;n Ã„Æ’n d&acirc;n d&atilde; thÃ¡Â»â€¹nh h&agrave;nh khÃ¡ÂºÂ¯p ba miÃ¡Â»ï¿½n, b&aacute;nh Ã„â€˜&uacute;c Ã„Æ’n gi&ograve;n, m&aacute;t, mÃ¡Â»â€¹n, no bÃ¡Â»Â¥ng m&agrave; lÃ¡ÂºÂ¡i dÃ¡Â»â€¦ ti&ecirc;u dÃ¡Â»â€¦ l&agrave;m v&agrave; gi&aacute; th&agrave;nh cÃ…Â©ng rÃ¡ÂºÂ¥t rÃ¡ÂºÂ». Kh&ocirc;ng chÃ¡Â»â€° Ã„â€˜Ã†Â°Ã¡Â»Â£c Ã„Æ’n nhÃ†Â° mÃ¡Â»â„¢t thÃ¡Â»Â©c qu&agrave; qu&ecirc;, bÃ¡Â»Â¯a Ã„Æ’n s&aacute;ng m&agrave; Ã„â€˜iÃ¡Â»Æ’n h&igrave;nh l&agrave; b&aacute;nh Ã„â€˜&uacute;c chÃ¡ÂºÂ¥m tÃ†Â°Ã†Â¡ng, b&aacute;nh Ã„â€˜&uacute;c cÃ…Â©ng c&oacute; thÃ¡Â»Æ’ Ã„Æ’n k&egrave;m vÃ¡Â»â€ºi canh ri&ecirc;u cua, rau thÃ†Â¡m, mÃ¡ÂºÂ¯m t&ocirc;m, mÃ¡ÂºÂ­t ong, mÃ¡ÂºÂ­t m&iacute;a, mÃ¡Â»Â©t tr&aacute;i c&acirc;y v&agrave; thÃ¡ÂºÂ­m ch&iacute; cÃ¡ÂºÂ£ c&aacute; kho, thÃ¡Â»â€¹t kho t&ugrave;y th&iacute;ch. TÃ¡Â»Â« loÃ¡ÂºÂ¡i b&aacute;nh Ã„â€˜&uacute;c thuÃ¡ÂºÂ§n t&uacute;y chÃ¡Â»â€° Ã„â€˜Ã†Â°Ã¡Â»Â£c bÃ¡ÂºÂ±ng bÃ¡Â»â„¢t gÃ¡ÂºÂ¡o pha vÃ¡Â»â€ºi nÃ†Â°Ã¡Â»â€ºc v&o