INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (97, 31, 3, 'Lantern Festival', '', 'lantern-festival', '<p style="text-align: justify;">The Lantern Festival or Yuan Xiao Festival (simplified Chinese: Ã¥â€¦Æ’Ã¥Â®ÂµÃ¨Å â€š; traditional Chinese: Ã¥â€¦Æ’Ã¥Â®ÂµÃ§Â¯â‚¬; pinyin: Yu&aacute;nxiÃ„ï¿½oji&eacute;) or Shang Yuan Festival (simplified Chinese: Ã¤Â¸Å Ã¥â€¦Æ’Ã¨Å â€š; traditional Chinese: Ã¤Â¸Å Ã¥â€¦Æ’Ã§Â¯â‚¬; pinyin: Sh&agrave;ngyu&aacute;nji&eacute;) in China or Chap Goh Meh (Chinese: Ã¥ï¿½ï¿½Ã¤Âºâ€�Ã¦Å¡ï¿½; PeÃŒï¿½h-Ã…ï¿½e-jÃ„Â«: chaÃŒï¿½p-gÃ…ï¿½-m&ecirc;; literally "the fifteen night") Festival in Malaysia and Singapore or "TÃ¡ÂºÂ¿t ThÃ†Â°Ã¡Â»Â£ng Nguy&ecirc;n" or "TÃ¡ÂºÂ¿t Nguy&ecirc;n Ti&ecirc;u"(Vietnamese: TÃ¡ÂºÂ¿t Nguy&ecirc;n ti&ecirc;u) in Vietnam; is a festival celebrated on the fifteenth day of the first month in the lunar year in the Chinese calendar, the last day of the Lunar Chinese New Year celebration.</p>', '<p style="text-align: justify;">It is not to be confused with the Mid-Autumn Festival, which is sometimes also known as the "Lantern Festival" in locations such as Singapore and Malaysia. During the Lantern Festival, children go out at night to temples carrying paper lanterns (simplified Chinese: Ã¥â€¦â€�Ã¥Â­ï¿½Ã§ï¿½Â¯; traditional Chinese: Ã¥â€¦â€�Ã¥Â­ï¿½Ã§â€¡Ë†; pinyin: t&ugrave;zidÃ„â€œng) and solve riddles on the lanterns (simplified Chinese: Ã§Å’Å“Ã§ï¿½Â¯Ã¨Â°Å“; traditional Chinese: Ã§Å’Å“Ã§â€¡Ë†Ã¨Â¬Å½; pinyin: cÃ„ï¿½idÃ„â€œngm&iacute;). It officially ends the Chinese New Year celebrations.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/amancms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Lantern Festival celebration in Shijiazhuang.</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In ancient times, the lanterns were fairly simple, for only the emperor and noblemen had large ornate ones; in modern times, lanterns have been embellished with many complex designs. For example, lanterns are now often made in shapes of animals.<br /><br />The Lantern Festival is also known as the Little New Year since it marks the end of the series of celebrations starting from the Chinese New Year.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/amancms/upload/news/admin/2010/08/4c7cd59084397_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Year 2008 Taiwan Lantern Festival in Tainan, Taiwan.</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">In some region and countries, this festival is also regarded as the Chinese version of St. Valentine''s Day, a day celebrating love and affection between lovers in Chinese tradition and culture.</p>', 'Wikipedia', '', '17:14:03', 1, 'admin');
