INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (99, 31, 3, 'Lantern Festival', '', 'lantern-festival', '<p style="text-align: justify;">The Lantern Festival or "TÃ¡ÂºÂ¿t ThÃ†Â°Ã¡Â»Â£ng Nguy&ecirc;n" or "TÃ¡ÂºÂ¿t Nguy&ecirc;n Ti&ecirc;u"(Vietnamese: TÃ¡ÂºÂ¿t Nguy&ecirc;n ti&ecirc;u) in Vietnam; is a festival celebrated on the fifteenth day of the first month in the lunar year in the Chinese calendar, the last day of the Lunar Chinese New Year celebration.</p>', '<p style="text-align: justify;">It is not to be confused with the Mid-Autumn Festival, which is sometimes also known as the "Lantern Festival" in locations such as Singapore and Malaysia. During the Lantern Festival, children go out at night to temples carrying paper lanterns and solve riddles on the lanterns. It officially ends the Chinese New Year celebrations.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/amancms/upload/news/admin/2010/08/4c7cd59188688_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>Lantern Festival celebration in Shijiazhuang.</em></p>\r\n<p style="text-align: center;"><em><br /></em></p>\r\n<p style="text-align: justify;">In ancient times, the lanterns were fairly simple, for only the emperor and noblemen had large ornate ones; in modern times, lanterns have been embellished with many complex designs. For example, lanterns are now often made in shapes of animals.<br /><br />The Lantern Festival is also known as the Little New Year since it marks the end of the series of celebrations starting from the Chinese New Year.</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/amancms/upload/news/admin/2010/08/4c7cd59084397_crop.JPG" alt="" /></p>\r\n<p style="text-align: center;"><em>Year 2008 Taiwan Lantern Festival in Tainan, Taiwan.</em></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: justify;">In some region and countries, this festival is also regarded as the Chinese version of St. Valentine''s Day, a day celebrating love and affection between lovers in Chinese tradition and culture.</p>', 'Wikipedia', '', '17:33:02', 1, 'admin');
INSERT INTO news_article_revision (revision_id, article_id, category_id, title, sub_title, slug, description, content, author, icons, created_date, created_user_id, created_user_name) VALUES (100, 33, 2, 'Canh chua', '', 'canh-chua', '<p>Canh chua (literally "sour soup") is a sour soup indigenous to the Mekong River region of southern Vietnam.</p>', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><img src="http://phuocnh/amancms/upload/news/admin/2010/08/4c7cd9c2e4dfa_crop.jpg" alt="" /></p>\r\n<p style="text-align: center;"><em>A bowl of <em>canh chua</em></em></p>\r\n<p>&nbsp;</p>\r\n<p>It is typically made with fish from the Mekong River, pineapple, amanes (and sometimes also other vegetables such as okra or bÃ¡ÂºÂ¡c h&agrave;), and bean sprouts, in a tamarind-flavored broth. It is garnished with the lemony-scented herb ng&ograve; &ocirc;m (Limnophila aromatica), caramelized garlic, and chopped scallions, as well as other herbs, according to the specific variety of canh chua; these other herbs may include rau rÃ„Æ’m (Vietnamese coriander), ng&ograve; gai (long coriander), and rau quÃ¡ÂºÂ¿ (Thai basil).<br /><br />The sour taste of the soup comes from tamarind, which is mixed with a small amount of hot water; the mixture is then stirred for a few moments to release all the essence, and the liquid (minus the tamarind seeds and other solids, which are discarded) is then added to the soup.<br /><br />When made in style of a hot pot, canh chua is called lÃ¡ÂºÂ©u canh chua.</p>', 'Wikipedia', '', '17:33:55', 1, 'admin');
